-- Hinzufügen der Produktspalten
ALTER TABLE [dbo].[temp_table] 
ADD [Z] INT;

-- Setzen der benötigten Variablen
DECLARE @multiply_x_y_func nvarchar(4000) = N'SELECT @z = @x * @y;';
DECLARE @multiply_x_y_param nvarchar(500) = N'@x INT, @y INT, @z INT OUTPUT';
DECLARE @x INT, @y INT, @z INT;

-- Anwenden der Funktion für jede Zeile mittels Cursor
DECLARE func_cursor CURSOR FOR
SELECT X, Y FROM [dbo].[temp_table];
OPEN func_cursor;
FETCH NEXT FROM func_cursor INTO @x, @y;
WHILE @@FETCH_STATUS = 0
BEGIN
    EXECUTE sp_executesql @multiply_x_y_func, @multiply_x_y_param, @x, @y, @z OUTPUT; -- Apply the function
    UPDATE [dbo].[temp_table] SET Z = @z WHERE CURRENT OF func_cursor; -- Update the new column with the result
    FETCH NEXT FROM func_cursor INTO @x, @y;
END
CLOSE func_cursor;
DEALLOCATE func_cursor;

-- SELECT der aktualisierten Tabelle
SELECT * FROM [dbo].[temp_table];

-- DROP der Produktspalte
ALTER TABLE [dbo].[temp_table]
DROP COLUMN Z;
