DECLARE @add_x_y_func nvarchar(4000) = N'SELECT @c = @a * @b;';
DECLARE @add_x_y_param nvarchar(500) = N'@a int, @b int, @c int OUTPUT';

DECLARE @result int;
EXEC sp_executesql @add_x_y_func, @add_x_y_param, 6, 7, @c = @result OUTPUT;
PRINT CONVERT(varchar, @result); -- Es sollte '42' rauskommen
